-- Recipe: 두부청국장죽
INSERT INTO recipe (name, recipe_category, description, created_by, created_at, instructions, avg_rating, view_count, ingredient_name)
    VALUES ('두부청국장죽', '밥', '죽에 청국장가루를 사용해서 구수한 맛이 짠맛을 대체하고 두부, 소고기, 채소를 넣어 씹는 맛과 음식의 색감으로 맛을 높여주었다.', 'ADMIN', SYSDATE, '1. 다진 쇠고기는 양념장에 버무려주고 두부는
