-- Recipe: 도라지 유자청생채
INSERT INTO recipe (name, recipe_category, description, created_by, created_at, instructions, avg_rating, view_count, ingredient_name)
    VALUES ('도라지 유자청생채', '반찬', '라임과 유자로 만든 소스의 새콤달콤한 맛 때문에 소금을 적게 사용할 수 있어요.', 'ADMIN', SYSDATE, '1. 통 도라지는 껍질을 벗긴다 6. 홍고추는 반으로 잘라 장식한다. 4. 썰어 놓은 청. 홍고추와 양파는 차가운
