-- Recipe: 오렌지 드레싱 타다키 부추샐러드
INSERT INTO recipe (name, recipe_category, description, created_by, created_at, instructions, avg_rating, view_count, ingredient_name)
    VALUES ('오렌지 드레싱 타다키 부추샐러드', '반찬', '오렌지와 레몬, 양파를 갈아서 드레싱을 만들면 간을 세게 하지 않아도 맛있게 즐길 수 있어요.', 'ADMIN', SYSDATE, '1. 소고기는 소금과 후춧가루로
